import React from "react";

export default function PostFilter({filter,}) {
  return (
    <div>
      <Input
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Search..."
      />
      <Select
        defaultValue={"Sort by"}
        options={[
          { value: "title", name: "By name" },
          { value: "body", name: "By description" },
        ]}
        value={selectedSort}
        onChange={sortPosts}
      />
    </div>
  );
}
