    const [post, setPost] = useState({title: ' ', body: ''})


    const addNewPost = (e) =>{
        e.preventDefault()
        const newPost = {
            ...post, id: Date.now()
        }
        setPost({title: '', body: ''})
    }
   
  return (
    <div>
      <form >
        <Input value={post.title} onChange={e => set(e.target.value)} placeholder="Header"/>
      
        <Input value={post.body} onChange={e=> setBody(e.target.value)} placeholder="body"/>
        <Button onClick={addNewPost}>Add post</Button>
      </form>
